import BoardRow from "./BoardRow";
import {useState} from 'react'


function Board(props) {
  const rows = [0, 1, 2, 3, 4, 5, 6, 7, 8];
  const [handler, setHandler] = useState(() => {
    let temp = [];
    for (let index = 0; index < 54; index++) {
        temp.push({Square : index , state : "white"});
    }
    return (temp);
  });
  console.log(handler);
  return (
    <div className="Board bg-secondary">
      <div className="d-flex flex-row gap-1 border">
        {rows.map((data) => (
          <BoardRow number={data} key={data}/>
        ))}
      </div>
    </div>
  );
}
export default Board;
