import { useEffect, useState } from "react";
import Square from "./Square";

function Board(props) {
    const [count,setCount] = useState(0);
    const squares = [];
    useEffect(()=>{for (let index = count; index < 3; index++) {
        setCount(count+1);
        squares.push(count);
    }console.log(squares)},[squares,count]);

    return (
        <div>
        {squares.map((data) => (<Square color="blue" id={data}/>))}
        </div>
    )
}
export default Board;
