import BoardCol from "./BoardCol";
import { useEffect, useState } from "react";
import Button from "react-bootstrap/Button";

function Board(props) {
  const Cols = [0, 1, 2, 3, 4, 5, 6, 7, 8];
  const [player, setplayer] = useState("#77834d");
  const initial = () => {
    let temp = [];
    let ttemp = [];
    for (let index = 0; index < 54; index++) {
      ttemp.push({ Square: index, state: "#efefee" });
      if ((index + 1) % 6 === 0) {
        temp.push(ttemp);
        ttemp = [];
      }
    }
    return temp;
  };
  const [game, setGame] = useState(true);
  const [BoardHandler, setBoardHandler] = useState(() => {
    let temp = [];
    let ttemp = [];
    for (let index = 0; index < 54; index++) {
      ttemp.push({ Square: index, state: "#efefee" });
      if ((index + 1) % 6 === 0) {
        temp.push(ttemp);
        ttemp = [];
      }
    }
    return temp;
  });
  const handlClick = (row) => {
    if (game) {
      let i;
      for (i = 5; i > -1 && BoardHandler[row][i].state !== "#efefee"; i--) {}
      let temp = player;
      setplayer(player === "#77834d" ? "#f44336" : "#77834d");
      setBoardHandler((prev) =>
        prev.map((data, index) =>
          index === row
            ? data.map((col, count) =>
                count === i ? { Square: col.Square, state: temp } : col
              )
            : data
        )
      );
    }
    const BoardCol = document.querySelector(".Board-Col");
    BoardCol.style.setProperty("--current-player", player);
  };
  const handleHoverEnter = (col) => {
    console.log("#Col" + col.toString() + "::before");
    const beforeSelectorRule =
      "#Col" +
      col.toString() +
      "::before {display : initial; background-color:" +
      player +
      ";}";
    var styleSheet = document.styleSheets[0];
    styleSheet.insertRule(beforeSelectorRule, styleSheet.cssRules.length);
  };
  const handleHoverLeave = (col) => {
    console.log("#Col" + col.toString() + "::before");
    const beforeSelectorRule =
      "#Col" + col.toString() + "::before {display : none;}";
    var styleSheet = document.styleSheets[0];
    styleSheet.insertRule(beforeSelectorRule, styleSheet.cssRules.length);
  };

  useEffect(() => {
    claculateWinner(BoardHandler) !== "No-winner yet"
      ? setGame(false)
      : setGame(true);
  }, [claculateWinner, BoardHandler]);
  return (
    <div className="d-flex flex-row gap-1 border Board bg-secondary">
      {Cols.map((data, index) => (
        <BoardCol
          number={data}
          key={data}
          status={BoardHandler[index]}
          onClick={(data) => handlClick(data)}
          onMouseEnter={(data) => handleHoverEnter(data)}
          onMouseLeave={(data) => handleHoverLeave(data)}
        />
      ))}
      <Button
        className="Reset"
        variant="dark"
        size="sm"
        onClick={() => setBoardHandler(initial)}
      >
        Reset{" "}
      </Button>
    </div>
  );
}

function claculateWinner(Board) {
  for (let i = 0; i < Board.length; i++) {
    for (let j = 0; j < Board[i].length; j++) {
      const temp = Board[i][j].state;
      if (temp != "#efefee") {
        if (
          j + 3 < Board[i].length &&
          [
            Board[i][j].state,
            Board[i][j + 1].state,
            Board[i][j + 2].state,
            Board[i][j + 3].state,
          ].toString() === [temp, temp, temp, temp].toString()
        ) {
          return temp;
        }
        if (
          i + 3 < Board.length &&
          [
            Board[i][j].state,
            Board[i + 1][j].state,
            Board[i + 2][j].state,
            Board[i + 3][j].state,
          ].toString() === [temp, temp, temp, temp].toString()
        ) {
          return temp;
        }
        if (
          i + 3 < Board.length &&
          j + 3 < Board[i].length &&
          [
            Board[i][j].state,
            Board[i + 1][j + 1].state,
            Board[i + 2][j + 2].state,
            Board[i + 3][j + 3].state,
          ].toString() === [temp, temp, temp, temp].toString()
        ) {
          return temp;
        }
        if (
          i - 3 >= 0 &&
          j - 3 >= 0 &&
          [
            Board[i][j].state,
            Board[i - 1][j - 1].state,
            Board[i - 2][j - 2].state,
            Board[i - 3][j - 3].state,
          ].toString() === [temp, temp, temp, temp].toString()
        ) {
          return temp;
        }
        if (
          i + 3 < Board.length &&
          j - 3 >= 0 &&
          [
            Board[i][j].state,
            Board[i + 1][j - 1].state,
            Board[i + 2][j - 2].state,
            Board[i + 3][j - 3].state,
          ].toString() === [temp, temp, temp, temp].toString()
        ) {
          return temp;
        }
        if (
          i - 3 >= 0 &&
          j + 3 < Board[i].length &&
          [
            Board[i][j].state,
            Board[i - 1][j + 1].state,
            Board[i - 2][j + 2].state,
            Board[i - 3][j + 3].state,
          ].toString() === [temp, temp, temp, temp].toString()
        ) {
          return temp;
        }
      }
    }
  }
  return "No-winner yet";
}
export default Board;
