    setShowAttributes(false);
    setTimeout(() => {
      setShowAttributes(true);
    }, 2500);
    console.log("asd")
  }
  if (showAttributes) {
  return (
    <div id="Attributes" className="bg-secondary">
      <p id="att">
        {props.winner === "#77834d"
          ? "First player wins"
          : "Second player wins"}{" "}
        <br /> click the reset button{" "}
      </p>
      <Button
        style={{ marginTop: "-10px" }}
        onClick={() => handleClick()}
      >
        See the Board
      </Button>
    </div>
  )}else {
    return (<div id="Attributes" style={{width: "0",height : "0" , border : "none"}}></div>);
  }
}
export default Attributes;
