import BoardRow from "./BoardRow";

function Board(props) {
  const rows = [0, 1, 2, 3, 4, 5];
  return (
    <div className="Board bg-primary">
      <div className="d-flex flex-column gap-1 border">
        {rows.map((data) => (
          <BoardRow number={data} />
        ))}
      </div>
    </div>
  );
}
export default Board;
