import { useState } from "react";
import { Button } from "react-bootstrap";
import Square from "./Square";

function Attributes(props) {
    const [display , setDisplay] = useState(props.style)
  return (
    <div id="Attributes" style={display} className="bg-secondary">
        <p id="att">{props.winner === "#60a569" ? "First player wins" : "Second player wins"} <br /> click the reset button </p>
        <Button style={{marginTop : "-10px"}} onClick={()=>{
          setDisplay({display : "none"});
          setTimeout(() => {setDisplay(props.style)},2500)
        }}>See the Board</Button>
    </div>
  );
}
export default Attributes;
