import BoardRow from "./BoardRow";
import {useState} from 'react'


function Board(props) {
  const rows = [0, 1, 2, 3, 4, 5, 6, 7, 8];
  const [BoardHandler, setBoardHandler] = useState(() => {
    let temp = [];
    let ttemp = [];
    for (let index = 0; index < 54; index++) {
        ttemp.push({Square : index , state : "white"});
        if ((index+1) % 6 === 0) {
            temp.push(ttemp);
            ttemp = [];
        }
    }
    return (temp);
  });
  console.log(BoardHandler);
  return (
    <div className="Board bg-secondary">
      <div className="d-flex flex-row gap-1 border">
        {rows.map((data,index) => (
          <BoardRow number={data} key={data} status={BoardHandler[index]}/>
        ))}
      </div>
    </div>
  );
}
export default Board;
