import BoardCol from "./BoardCol";
import { useEffect, useState } from "react";
import Button from 'react-bootstrap/Button';

function Board(props) {
  const rows = [0, 1, 2, 3, 4, 5, 6, 7, 8];
  const [player, setplayer] = useState("blue");
  const initial = () => {
    let temp = [];
    let ttemp = [];
    for (let index = 0; index < 54; index++) {
      ttemp.push({ Square: index, state: "white" });
      if ((index + 1) % 6 === 0) {
        temp.push(ttemp);
        ttemp = [];
      }
    }
    return temp;
  };
  const [game, setGame] = useState(true);
  const [BoardHandler, setBoardHandler] = useState(() => {
    let temp = [];
    let ttemp = [];
    for (let index = 0; index < 54; index++) {
      ttemp.push({ Square: index, state: "white" });
      if ((index + 1) % 6 === 0) {
        temp.push(ttemp);
        ttemp = [];
      }
    }
    return temp;
  });
  const handlClick = (row) => {
    if (game) {
      let i;
      for (i = 5; i > -1 && BoardHandler[row][i].state !== "white"; i--) {}
      let temp = player;
      setplayer(player === "blue" ? "red" : "blue");
      setBoardHandler((prev) =>
        prev.map((data, index) =>
          index === row
            ? data.map((col, count) =>
                count === i ? { Square: col.Square, state: temp } : col
              )
            : data
        )
      );
    }
  };
  const handleHover = () => {

  };

  useEffect(() => {
    claculateWinner(BoardHandler) !== "No-winner yet"
      ? setGame(false)
      : setGame(true);
  }, [claculateWinner, BoardHandler]);
  return (
      <div className="d-flex flex-row gap-1 border Board bg-dark">
        {rows.map((data, index) => (
          <BoardCol
            number={data}
            key={data}
            status={BoardHandler[index]}
            onClick={(data) => handlClick(data)}
            onHover={() => handleHover}
          />
        ))}
      <Button className="Reset" variant="outline-secondary" onClick={() => setBoardHandler(initial)}>Reset </Button>
      </div>

  );
}

function claculateWinner(Board) {
  for (let i = 0; i < Board.length; i++) {
    for (let j = 0; j < Board[i].length; j++) {
      const temp = Board[i][j].state;
      if (temp != "white") {
        if (
          j + 3 < Board[i].length &&
          [
            Board[i][j].state,
            Board[i][j + 1].state,
            Board[i][j + 2].state,
            Board[i][j + 3].state,
          ].toString() === [temp, temp, temp, temp].toString()
        ) {
          return temp;
        }
        if (
          i + 3 < Board.length &&
          [
            Board[i][j].state,
            Board[i + 1][j].state,
            Board[i + 2][j].state,
            Board[i + 3][j].state,
          ].toString() === [temp, temp, temp, temp].toString()
        ) {
          return temp;
        }
        if (
          i + 3 < Board.length &&
          j + 3 < Board[i].length &&
          [
            Board[i][j].state,
            Board[i + 1][j + 1].state,
            Board[i + 2][j + 2].state,
            Board[i + 3][j + 3].state,
          ].toString() === [temp, temp, temp, temp].toString()
        ) {
          return temp;
        }
        if (
          i - 3 >= 0 &&
          j - 3 >= 0 &&
          [
            Board[i][j].state,
            Board[i - 1][j - 1].state,
            Board[i - 2][j - 2].state,
            Board[i - 3][j - 3].state,
          ].toString() === [temp, temp, temp, temp].toString()
        ) {
          return temp;
        }
        if (
          i + 3 < Board.length &&
          j - 3 >= 0 &&
          [
            Board[i][j].state,
            Board[i + 1][j - 1].state,
            Board[i + 2][j - 2].state,
            Board[i + 3][j - 3].state,
          ].toString() === [temp, temp, temp, temp].toString()
        ) {
          return temp;
        }
        if (
          i - 3 >= 0 &&
          j + 3 < Board[i].length &&
          [
            Board[i][j].state,
            Board[i - 1][j + 1].state,
            Board[i - 2][j + 2].state,
            Board[i - 3][j + 3].state,
          ].toString() === [temp, temp, temp, temp].toString()
        ) {
          return temp;
        }
      }
    }
  }
  return "No-winner yet";
}
export default Board;
