  return (
    <div className="Board bg-secondary">
      <div className="d-flex flex-row gap-1 border">
        {rows.map((data) => (
          <BoardRow number={data} onClick={handleClick}/>
        ))}
      </div>
    </div>
  );
}
export default Board;
