import { ReactComponent as Logo } from './MainLogo.svg';
import { useEffect, useState } from 'react';
import './App.css';
import './App.scss';
import Attributes from './Components/Attributes';
import Board from './Components/Board';
import ScreenWarning from './Components/ScreenWarning';


function App() {
  const [winner , setWinner] = useState(null);
  const [player , setPlayer] = useState(null);
  const getData = (winner,player) => {
    if (winner !== "No-winner yet") {
    setWinner(winner);
    }
    setPlayer(player);
  }
  let screen;
  useEffect(() => {screen =(! /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini|Mobile|mobile|CriOS/i.test(window.navigator.userAgent) ||
  window.orientation === 0 || window.orientation === 180)},[screen]);
  return (
    <div className="App">
      { screen  ?
      <div>
      <center><Board getData={(winner,player)=>getData(winner,player)}/></center>
      <Attributes winner={winner} player={player}/>
      <Logo fill='#8f8fa3' id="logo"/>
      </div>
       : 
       <ScreenWarning/>}
    </div>
  );
}

export default App;
