import BoardCol from "./BoardCol";
import { useEffect, useState } from "react";



function Board(props) {
  const rows = [0, 1, 2, 3, 4, 5, 6, 7, 8];
  const [player , setplayer] = useState("blue");
  const [BoardHandler, setBoardHandler] = useState(() => {
    let temp = [];
    let ttemp = [];
    for (let index = 0; index < 54; index++) {
      ttemp.push({ Square: index, state: "white" });
      if ((index + 1) % 6 === 0) {
        temp.push(ttemp);
        ttemp = [];
      }
    }
    return temp;
  });
  const handlClick = (row) => {
    let i;
    for (i = 5 ; i > -1 && BoardHandler[row][i].state !== "white";i--){
    }
    let temp = player;
    setplayer(player === "blue" ? "red" : "blue");
    setBoardHandler((prev)=> (prev.map((data,index) => 
        index === row 
            ? data.map((col,count) => (count === i ? {Square : count*(index+1)+1,state : temp} : col))
            : data
    )));
    console.log(claculateWinner(BoardHandler));
  }
  return (
    <div className="Board bg-secondary">
      <div className="d-flex flex-row gap-1 border">
        {rows.map((data, index) => (
          <BoardCol number={data} key={data} status={BoardHandler[index]} onClick={(data) => handlClick(data)}/>
        ))}
      </div>
    </div>
  );
}

function claculateWinner(Board) {
    console.log(Board);
    for (let i = 0 ; i < Board.length ; i++) {
        for (let j = 0 ; j < Board[i].length ; j++) {
            const temp = Board[i][j].state;
            if ( temp != "white") {
                if (j+3 < Board[i].length && [Board[i][j].state,Board[i][j+1].state,Board[i][j+2].state,Board[i][j+3].state] === [temp,temp,temp,temp]) {
                    return temp;
                }
                if (i+3 < Board.length && [Board[i][j].state,Board[i+1][j].state,Board[i+2][j].state,Board[i+3][j].state] === [temp,temp,temp,temp]) {
                    return temp;
                }
                if (i+3 < Board.length && j+3 < Board[i].length && [Board[i][j].state,Board[i+1][j+1].state,Board[i+2][j+2].state,Board[i+3][j+3].state] === [temp,temp,temp,temp]) {
                    return temp;
                }
            }
        }
    }
    return "No-winner yet";
}
export default Board;
